package naveen3;

public class last_character {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name1 = "Naveen Kumar Is one of the best Automation Engineer That Simplilearn has ever produced";
		
		System.out.println("Length is" + name1.length());
		
		System.out.println(" The last character in the sentence is: " + name1.charAt(name1.length()-1));
		
		System.out.println(name1.charAt(85));
		
		

	}

}
