package naveen4;

public class Array_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr1 = {10,20,30,40,50};
		
		System.out.println("The length of array is " + arr1.length);
		
		System.out.println("The first item in array is " + arr1[0]);
		
		System.out.println("The last letter in array is " + arr1[arr1.length - 1]);
		
		String[] arr2 = {"mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"};
		
		System.out.println("The value at 3rd position is " + arr2[2]);
		
		System.out.println("Length of 2nd Array is " + arr2.length);
		
		System.out.println("The last item in array 2 is " + arr2[arr2.length - 1]);

	}

}
